mapper.select:
select 

from tb_order_detail
where order_no = #{orderNo,jdbcType=CHAR}
  and product_id = #{productId,jdbcType=CHAR}

mapper.delete:
delete from tb_order_detail
where order_no = #{orderNo,jdbcType=CHAR}
  and product_id = #{productId,jdbcType=CHAR}

mapper.insert:
insert into tb_order_detail (order_no, product_id, price, 
  sale_price, count, mch_id
  )
values (#{orderNo,jdbcType=CHAR}, #{productId,jdbcType=CHAR}, #{price,jdbcType=DECIMAL}, 
  #{salePrice,jdbcType=DECIMAL}, #{count,jdbcType=INTEGER}, #{mchId,jdbcType=CHAR}
  )

mapper.update:
update tb_order_detail
set price = #{price,jdbcType=DECIMAL},
  sale_price = #{salePrice,jdbcType=DECIMAL},
  count = #{count,jdbcType=INTEGER},
  mch_id = #{mchId,jdbcType=CHAR}
where order_no = #{orderNo,jdbcType=CHAR}
  and product_id = #{productId,jdbcType=CHAR}
